import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import DataTable from './DataTable';
import RegionNN from './RegionNN';
import RegionBH from './RegionBH';
import RegionKS from './RegionKS';
import RegionAZ from './RegionAZ';
import './App.css';  // Импортируем файл стилей

const App = () => {
  const [region, setRegion] = useState('NN');

  return (
    <Router>
      <div className="app-container">
        <nav className="navbar">
          <ul className="nav-list">
            <li className="nav-item"><Link className={`nav-link ${region === 'NN' ? 'active' : ''}`} to="/region-nn" onClick={() => setRegion('NN')}>Region NN</Link></li>
            <li className="nav-item"><Link className={`nav-link ${region === 'BH' ? 'active' : ''}`} to="/region-bh" onClick={() => setRegion('BH')}>Region BH</Link></li>
            <li className="nav-item"><Link className={`nav-link ${region === 'KS' ? 'active' : ''}`} to="/region-ks" onClick={() => setRegion('KS')}>Region KS</Link></li>
            <li className="nav-item"><Link className={`nav-link ${region === 'AZ' ? 'active' : ''}`} to="/region-az" onClick={() => setRegion('AZ')}>Region AZ</Link></li>
          </ul>
        </nav>

        <Routes>
          <Route path="/" element={<DataTable />} />
          <Route path="/region-nn" element={<RegionNN />} />
          <Route path="/region-bh" element={<RegionBH />} />
          <Route path="/region-ks" element={<RegionKS />} />
          <Route path="/region-az" element={<RegionAZ />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
