import React, { useState, useEffect } from 'react';
import axios from 'axios';
import exportToExcel from './exportToExcel';
import './RegionNN.css';

const RegionNN = ({ data, onEdit }) => {
  const [regionData, setRegionData] = useState([]);
  const [showAll, setShowAll] = useState(false);

  useEffect(() => {
    if (showAll) {
      axios.get('http://localhost:5000/api/weather/nn/all')
        .then(response => {
          setRegionData(response.data);
        })
        .catch(error => {
          console.error('Error fetching data: ', error);
        });
    } else {
      setRegionData(data);
    }
  }, [showAll, data]);

  const handleShowAll = () => {
    setShowAll(true);
  };

  const handleHideAll = () => {
    setShowAll(false);
  };

  const handleExport = () => {
    exportToExcel('nn');
  };

  return (
    <div className="region-container">
      <h2>Region NN</h2>
      <table className="region-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Timestamp</th>
            <th>Temperature</th>
            <th>Pressure</th>
            <th>Humidity</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {regionData.map(item => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.timestamp}</td>
              <td>{item.temperature_nn}</td>
              <td>{item.pressure_nn}</td>
              <td>{item.humidity_nn}</td>
              <td><button onClick={() => onEdit(item)}>Edit</button></td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleExport}>Export to Excel</button>
      {!showAll ? (
        <button onClick={handleShowAll}>Show All</button>
      ) : (
        <button onClick={handleHideAll}>Hide</button>
      )}
    </div>
  );
};

export default RegionNN;
