import React, { useState, useEffect } from 'react';
import axios from 'axios';

const WeatherTable = () => {
  const [dataNN, setDataNN] = useState([]);
  const [dataBH, setDataBH] = useState([]);
  const [showAllNN, setShowAllNN] = useState(false);

  useEffect(() => {
    fetchDataNN();
    fetchDataBH();
  }, []);

  const fetchDataNN = async () => {
    const endpoint = showAllNN ? 'api/weather/nn/all' : 'api/weather/nn';
    const result = await axios(endpoint);
    setDataNN(result.data);
  };

  const fetchDataBH = async () => {
    const result = await axios('api/weather/bh');
    setDataBH(result.data);
  };

  const handleShowAllNN = () => {
    setShowAllNN(!showAllNN);
    fetchDataNN();
  };

  return (
    <div>
      <h2>Region NN</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Temperature</th>
            <th>Pressure</th>
            <th>Humidity</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {dataNN.map((row) => (
            <tr key={row.id}>
              <td>{row.temperature_nn}</td>
              <td>{row.pressure_nn}</td>
              <td>{row.humidity_nn}</td>
              <td><button>Edit</button></td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleShowAllNN}>
        {showAllNN ? 'Hide All' : 'Show All'}
      </button>

      <h2>Region BH</h2>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Timestamp</th>
            <th>Temperature</th>
            <th>Pressure</th>
            <th>Humidity</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {dataBH.map((row) => (
            <tr key={row.id}>
              <td>{row.id}</td>
              <td>{row.timestamp}</td>
              <td>{row.temperature_bh}</td>
              <td>{row.pressure_bh}</td>
              <td>{row.humidity_bh}</td>
              <td><button>Edit</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default WeatherTable;
