import axios from 'axios';
import XLSX from 'xlsx';

const exportToExcel = async (region) => {
  try {
    const response = await axios.get(`http://localhost:5000/api/weather/${region}/all`);
    const data = response.data;

    const ws = XLSX.utils.json_to_sheet(data);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, `Region ${region.toUpperCase()}`);

    XLSX.writeFile(wb, `Region_${region.toUpperCase()}_Data.xlsx`);
  } catch (error) {
    console.error('Error exporting data: ', error);
  }
};

export default exportToExcel;
